package leetcode.contests.contest_126;

public class ValidAfterSubstitution {
    public static void main(String[] args) {
        System.out.println(isValid("aaabcbcbc"));
        System.out.println(isValid("aabcbc"));
        System.out.println(isValid("aabcbcabc"));
        System.out.println(isValid("abcabcababcc"));
        System.out.println(isValid("cababc"));
        System.out.println(isValid(""));
    }

    public static boolean isValid(String S) {
        if(S.length()<3) return false;
        String left="";
        while (S.length()>3){
            int hit=S.indexOf("abc");
            if(hit<0) return false;
            if(hit==0){
                S=S.substring(hit+3);
                System.out.println(S);
            }else{
                S= S.substring(0,hit)+S.substring(hit+3);
            }
        }
        if(left.concat(S).equalsIgnoreCase("ABC")) return true;
        return false;
    }
}
